import { useParams } from 'react-router-dom';
import axios from 'axios';
import React, { useState, useEffect } from 'react';
import toast from 'react-hot-toast';

const Project = () => {
  const { projectId } = useParams();
  const [newTodo, setNewTodo] = useState('');
  const [todoList, setTodoList] = useState([]);

  const handleAddTodo = async () => {
    try {
        
        const { projectId } = useParams();
        const response = await fetch('http://localhost:5000/api/todos/addTodo', {projectId},{
            method: 'POST',
            // headers: {
            //     'Content-Type': 'application/json',
            //     '
            // },
            body: JSON.stringify({ title: newTodo })
        });

        if (!response.ok) {
            throw new Error(`HTTP error! Status: ${response.status}`);
        }

        const responseData = await response.json();
        console.log('Todo added successfully:', responseData);

        // Optionally, clear the input field after successful submission
        setNewTodo("");
    } catch (error) {
        console.error('Error adding project:', error.message);
        // You can display an error message to the user if needed
    }
};

const getTasks = async () => {
    try {
      const { data } = await axios.get('/api/tasks/mytodos');
      setTodoList(
        data.sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt)),
      );
    } catch (err) {
      console.log(err);
    }
  };

  useEffect(() => {
    getTasks();
  }, []);


  const deleteTask = async (id) => {
    try {
      await axios.delete(`/api/todos/${id}`);
      toast.success('Task deleted');
      setTaskList(taskList.filter((task) => task._id !== id));
    } catch (err) {
      console.log(err);
    }
  };


  return (
    // <div>
    //   <h2>Project Details</h2>
    //   <p>Project ID: {projectId}</p>
    //   {/* Fetch project details using projectId */}
    // </div>

<div>

  <div className='create_form'>
  <input type="text" placeholder='Enter todo Name' onChange={(e) => setNewTodo(e.target.value)}/>
  <button type="button" onClick={handleAddTodo}>Add</button>

</div>



{todoList.length > 0 ? (
  <table className={classes.taskList_table}>
    <tbody>
      {todoList.map((todo) => (
        <TodoItem key={todo._id} todo={todo} deleteTodo={deleteTodo} />
      ))}
    </tbody>
  </table>
) : (
  'No Todo Found. Create a new todo'
)}
</div>
);
}
  

export default Project;




function TaskList() {
  const [taskList, setTaskList] = useState([]);
  const [isAddingNew, setIsAddingNew] = useState(false);
  const [newTask, setNewTask] = useState('');

  const getTasks = async () => {
    try {
      const { data } = await axios.get('/api/tasks/mytasks');
      setTaskList(
        data.sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt)),
      );
    } catch (err) {
      console.log(err);
    }
  };

  useEffect(() => {
    getTasks();
  }, []);

  const addNewButtonClick = () => {
    setIsAddingNew(!isAddingNew);
  };

  const addNewTask = async (e) => {
    e.preventDefault();
    if (newTask.length <= 0) {
      toast.error('Task is empty');
      return;
    }
    try {
      const { data } = await axios.post('/api/tasks/', {
        title: newTask,
      });
      toast.success('New task added');
      setIsAddingNew(false);
      setNewTask('');
      setTaskList([{ ...data }, ...taskList]);
    } catch (err) {
      console.log(err);
    }
  };

  const deleteTask = async (id) => {
    try {
      await axios.delete(`/api/tasks/${id}`);
      toast.success('Task deleted');
      setTaskList(taskList.filter((task) => task._id !== id));
    } catch (err) {
      console.log(err);
    }
  };

  return (
    <div>
      <div className={classes.topBar}>
        <button
          type="button"
          className={classes.addNew}
          onClick={addNewButtonClick}
        >
          Add New
        </button>
      </div>
      {isAddingNew && (
        <form className={classes.addNewForm} onSubmit={addNewTask}>
          <input
            type="text"
            value={newTask}
            onChange={(e) => setNewTask(e.target.value)}
            placeholder="Task name"
          />
          <button type="submit">Add</button>
        </form>
      )}
      {taskList.length > 0 ? (
        <table className={classes.taskList_table}>
          <tbody>
            {taskList.map((task) => (
              <TaskItem key={task._id} task={task} deleteTask={deleteTask} />
            ))}
          </tbody>
        </table>
      ) : (
        'No Task Found. Create a new task'
      )}
    </div>
  );
}
